<style lang="scss">
  .key-lime-pie {
    &__container {
      position: fixed;
      inset: 0;
      background-color: rgba(0, 0, 0, 0.4);
      display: flex;
      justify-content: center;
      align-items: center;
    }

    &__icon {
      position: fixed;
      bottom: 0;
      left: 0;
      border: 0;
      background-color: transparent;
      margin-bottom: 0;
    }

    &__icon-image {
      width: 2em;
      height: 2em;
    }
  }
</style>

<script lang="ts">
  import KeyLimePieInfoBox from './KeyLimePieInfoBox.svelte';
  import Core from '../class/Core';
  import Status from '../enum/Status';
  import KeyLimePieCookieSelector from './KeyLimePieCookieSelector.svelte';

  const { status } = Core;

  function callCookieSelector() {
    status.set(Status.COOKIES_SELECTOR);
  }
</script>

{#if $status !== Status.NO_COOKIES}
  <div class="key-lime-pie">
    {#if $status === Status.ICON}
      <button
        class="key-lime-pie__icon"
        on:click={() => {
          callCookieSelector();
        }}
      >
        <img
          class="key-lime-pie__icon-image"
          alt="cookie conscent"
          src="data:image/png;base64,"
        />
      </button>
    {:else}
      <div class="key-lime-pie__container">
        {#if $status === Status.INITIAL_ASK}
          <KeyLimePieInfoBox />
        {/if}
        {#if $status === Status.COOKIES_SELECTOR}
          <KeyLimePieCookieSelector />
        {/if}
      </div>
    {/if}
  </div>
{/if}
